	public static final Pattern StringNoEmpezadoEnEspacioPattern = Pattern.compile("^\\w+.*");
	
	public static boolean esLongitudValida(String text, int longitudMinima, int longitdMaxima) {
		boolean esValido = false;

		if (text == null)
			throw new NullPointerException();

		if ((longitudMinima < 0 || longitudMinima < 0) || (longitudMinima > longitdMaxima))
			throw new IllegalArgumentException("Los valores de longitudes no son válidos");

		if (text.length() >= longitudMinima && text.length() <= longitdMaxima)
			esValido = true;

		return esValido;
	}
	
	public static boolean esIntValido(String text) {
		boolean esValido = true;
		try {
			Integer.parseInt(text);
		}
		catch (Exception e){
			esValido = false;
		}		
		return esValido;		
	}
	
	public static boolean esStringNoEmpezadoEnEspacios(String text, int longitudMaxima) {
		return text.length()<=longitudMaxima && StringNoEmpezadoEnEspacioPattern.matcher(text).matches();
	}
	
	public static boolean esMailValido(String text, int longitudMaxima) {
		return text.length()<=longitudMaxima && EmailRegexPattern.matcher(text).matches();
	}
	
	public static boolean esFechaNacimientoValida(Date fecha) {
		boolean ret = false;
		
		Calendar calFechaHoy = Calendar.getInstance();
		int añoHoy = calFechaHoy.get(Calendar.YEAR);
		int mesHoy = calFechaHoy.get(Calendar.MONTH);
		int diaHoy = calFechaHoy.get(Calendar.DATE);
		
		
		Calendar calFecha = Calendar.getInstance();
		calFecha.setTime(fecha);
		int añoFecha = calFecha.get(Calendar.YEAR);
		int mesFecha = calFecha.get(Calendar.MONTH);
		int diaFecha = calFecha.get(Calendar.DATE);
		
		return ret;
		
	}

